import bodyParser from "body-parser";
import dotenv from "dotenv";
import express from "express";

import TruffleConfig from "@truffle/config";
import type { ProjectInfo } from "@truffle/decoder";

import { getProjectInfoForTransaction } from "../src";

dotenv.config();

const app = express();
app.use(bodyParser.urlencoded({ extended: false }));
app.use(bodyParser.json());
app.get("/tx", handle);
console.log(`Starting on port ${process.env.PORT}`);

async function handle(req, res) {
  // ?from=0x4b203f54429f7d3019c0c4998b88f8f3517f8352&to=0x28de5c5f56b6216441ee114e832808d5b9d4a775&
  const txParams = {
    from: req.params.from,
    to: req.params.to,
    input: req.params.data,
    blockNumber: null
  };

  const config = TruffleConfig.default().merge({
    networks: {
      mainnet: {
        url: process.env["MAINNET_URL"],
        network_id: 1
      }
    },
    compilers: {
      solc: {
        docker: true
      }
    },
    network: "mainnet",
    // May need to make a more proper truffle config, will see. TODO
    sourceFetchers: ["etherscan"],
    etherscan: {
      apiKey: process.env["ETHERSCAN_KEY"]
    }
  });

  try {
    const projectInfo: ProjectInfo = await getProjectInfoForTransaction({
      to: txParams.to,
      networkId: "1",
      config
    });

    res.status(200).json(projectInfo);
  } catch (err) {
    res.status(400).json(err.message);
  }
}

app.listen(process.env.PORT);
